	/**
	 * Get dynamic heading for the extension popup depending on license state
	 *
	 * @since 2.2.0
	 */
	public static function get_extension_popup_dynamic_heading( $extension_title ) {
		if ( empty( ctf_license_handler()->get_license_key ) ) {
			return sprintf( __( 'Activate your License to %s', 'custom-twitter-feeds' ), $extension_title );
		} else {
			if ( ctf_license_handler()->expiredLicenseWithGracePeriodEnded ) {
				return sprintf( __( 'Renew license to %s', 'custom-twitter-feeds' ), $extension_title );
			} else {
				return sprintf( __( 'Upgrade to Pro to %s', 'custom-twitter-feeds' ), $extension_title );
			}
		}
	}

	/**
	 * Get dynamic upgrade/activate/renew URL depending on license state
	 *
	 * @since 2.2.0
	 */
	public static function get_extension_popup_dynamic_buy_url( $default_upgrade_url ) {
		$license_key = ctf_license_handler()->get_license_key;
		$license_notice_active = empty( ctf_license_handler()->get_license_key ) || ctf_license_handler()->expiredLicenseWithGracePeriodEnded ? true : false;
		// if the license is inactive
		if ( empty( ctf_license_handler()->get_license_key ) ) {
			return admin_url('admin.php?page=ctf-settings&focus=license');
		}
		// if the license is active but expired and grace period ended
		if ( $license_notice_active ) {
			return ctf_license_handler()->get_renew_url;
		}
		return $default_upgrade_url;
	}

	/**
	 * Get Account Info
	 * Screen Name + Avatar
	 *
	 *
	 * @since 2.0
	 */
	public static function get_account_info( $auth ){
		$consumer_key = ! empty( $auth['consumer_key'] ) ? $auth['consumer_key'] : 'FPYSYWIdyUIQ76Yz5hdYo5r7y';
		$consumer_secret = ! empty( $auth['consumer_secret'] ) ? $auth['consumer_secret'] : 'GqPj9BPgJXjRKIGXCULJljocGPC62wN2eeMSnmZpVelWreFk9z';
		$request_settings = array(
			'consumer_key' => $consumer_key,
			'consumer_secret' =>  $consumer_secret,
			'access_token' =>  $auth['access_token'],
			'access_token_secret' =>  $auth['access_token_secret']
		);
		$request_method = 'auto';
		$twitter_api = new CtfOauthConnectPro( $request_settings, 'accountlookup' );
		$twitter_api->setUrlBase();
		$twitter_api->setRequestMethod( $request_method );
		$twitter_api->performRequest();
		$response = json_decode( $twitter_api->json , $assoc = true );
		if( isset( $response['errors'] ) ){
	    	return [
	    		'error' => true
	    	];
	    }else{
	    	return[
	    		'account_handle' => isset( $response['screen_name'] ) ? sanitize_text_field($response['screen_name']) : '',
		        'account_avatar' =>isset( $response['profile_image_url'] ) ? sanitize_text_field($response['profile_image_url']) : ''
	    	];
	    }
	}

	/**
	 * Get WP Pages List
	 *
	 * @return array
	 *
	 * @since 2.0
	 */
	public function get_wp_pages(){
		$pagesList = get_pages();
		$pagesResult = [];
		if(is_array($pagesList)){
			foreach ($pagesList as $page) {
				array_push($pagesResult, ['id' => $page->ID, 'title' => $page->post_title]);
			}
		}
		return $pagesResult;
	}


